//   left: 30px;
//   top: 220px;
//   line-height: 35px;
// `;

export const UploadBanner = () => {
  return (
    <BannerContainer>
      <BannerImage src={projectsimage}></BannerImage>
      <BannerHeader>UPLOAD</BannerHeader>
      {/* <BannerParagraph>
        Bootcamp projects worth seeing.
        <br />
        And some more text
      </BannerParagraph> */}
    </BannerContainer>
  );
};
